package ficheros_directorios;

import java.io.*;

public class Creando {

	public static void main(String[] args) {
		// TODO Auto-generated method stu
		
	
		
		File ruta = new File("/home/abelardo/Escritorio/prueba_texto.txt");
		
		String archivo_destino= ruta.getAbsolutePath(); 
		//ruta.mkdir();
		
		try {
			
			ruta.createNewFile();
			
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	
		
		Escribiendo accede_es = new Escribiendo();
		
		accede_es.escribir(archivo_destino);
		
	}

}


class Escribiendo{
	
	
	public void escribir(String ruta) {
		
		
		String frase = "Esto es un ejemplo. Espero que salga";
		
		try {
			
			FileWriter escritura = new FileWriter(ruta);
			
			for (int i = 0 ; i < frase.length(); i++) {
				
				escritura.write(frase.charAt(i));
				
			}
			
			escritura.close();
		}catch(IOException e){
			
		}
		
	}
	
}























































































































































































































































































































































































